mapper.select:
SELECT MAIN_INCOME MAIN_INCOME1
FROM FIL_CREDIT_FINANCE_PROFITDIS
WHERE IN_DATE = to_char(add_months(TO_DATE(#{IN_DATE_}, 'YYYY-MM-DD'),
-12),'YYYY-MM-DD')
AND PEOPLE_ID=#{CLIENT_ID}
AND HY_ID=#{INDUSTRY_ID}

SELECT NET_TOTAL NET_TOTAL1
FROM FIL_CREDIT_FINANCE_DEBT
WHERE IN_DATE = TO_CHAR((TRUNC(TO_DATE(#{IN_DATE}, 'YYYY-MM-DD'), 'YYYY')),
'YYYY-MM-DD')
AND PEOPLE_ID=#{CLIENT_ID}
AND HY_ID=#{INDUSTRY_ID}

mapper.delete:
DELETE FROM FIL_CREDIT_FINANCE_PROFITDIS WHERE IDENTIFIERS=#{MANAGEID}
AND PEOPLE_ID=#{PEOPLE_ID}
AND HY_ID=#{INDUSTRY_ID}

DELETE FROM FIL_CREDIT_FINANCE_CASH WHERE IDENTIFIERS = #{MANAGEID} 
AND PEOPLE_ID=#{PEOPLE_ID}
AND HY_ID=#{INDUSTRY_ID}

DELETE FROM FIL_CREDIT_FINANCE_ANALYZE WHERE IDENTIFIERS = #{MANAGEID}
AND CLIENT_ID=#{PEOPLE_ID}
AND HY_ID=#{INDUSTRY_ID}

mapper.insert:
INSERT INTO FIL_CREDIT_FINANCE_MANAGE (
ID
,OPERATOR_ID
,OPERATOR_TIME
,OPERATOR_NAME

,OPERATOR_CODE

,PEOPLE_ID
,INDUSTRY_ID
,IN_DATE1
,IN_DATE2
,IN_DATE3
) VALUES (
#{ID}
,#{OPERATOR_ID}

,TO_CHAR(SYSDATE,'YYYY-MM-DD HH:MI:SS')
,#{OPERATOR_NAME}

,#{OPERATOR_CODE}

,#{PEOPLE_ID}
,#{INDUSTRY_ID}
,TO_DATE(#{IN_DATE1},'yyyy-MM-dd')

,TO_DATE(#{IN_DATE2},'yyyy-MM-dd')

,TO_DATE(#{IN_DATE3},'yyyy-MM-dd')

)

mapper.select:
SELECT COUNT(*) 
FROM FIL_CREDIT_FINANCE_MANAGE 
WHERE ID=#{ID} 
AND PEOPLE_ID=#{PEOPLE_ID} 
AND INDUSTRY_ID=#{INDUSTRY_ID}

mapper.delete:
DELETE FROM FIL_CREDIT_FINANCE_MANAGE WHERE ID=#{ID} 
AND PEOPLE_ID=#{PEOPLE_ID}
AND INDUSTRY_ID=#{INDUSTRY_ID}

mapper.update:
UPDATE FIL_CREDIT_FINANCE_MANAGE T SET T.ID=#{ID}
,OPERATOR_ID=#{OPERATOR_ID}
,OPERATOR_TIME=TO_DATE(#{OPERATOR_TIME},'yyyy-MM-dd')
,OPERATOR_NAME=#{OPERATOR_NAME}
,OPERATOR_CODE=#{OPERATOR_CODE}
,PEOPLE_ID=#{PEOPLE_ID}
,INDUSTRY_ID=#{INDUSTRY_ID}
,IN_DATE1=TO_DATE(#{IN_DATE1},'yyyy-MM-dd')
,IN_DATE2=TO_DATE(#{IN_DATE2},'yyyy-MM-dd')
,IN_DATE3=TO_DATE(#{IN_DATE3},'yyyy-MM-dd')
WHERE T.ID=#{ID} 
AND PEOPLE_ID=#{PEOPLE_ID}
AND INDUSTRY_ID=#{INDUSTRY_ID}
