mapper.select:
SELECT T2.* FROM ( SELECT T1.*,ROWNUM RN FROM ( SELECT T.ID, T.PRO_CODE, T.CUST_NAME, T.SUPP_NAME, T.EQU_NAME, T.EQU_TYPE, T.CUST_NAME_END, T.FACTORY_NO, T.CAR_SYMBOL, TO_CHAR(T.START_CONFIRM_DATE,'YYYY-MM-DD') START_CONFIRM_DATE, TO_CHAR(T.PRO_END_DATE,'YYYY-MM-DD') PRO_END_DATE, TO_CHAR(T.EST_DATE, 'YYYY-MM-DD') EST_DATE, TO_CHAR(T.YEARNUM)||'æœˆ' YEARNUM, T.INS_COM_NAME, T.INS_CODE, TO_CHAR(T.INS_START,'YYYY-MM-DD') INS_START, TO_CHAR(T.INS_END,'YYYY-MM-DD') INS_END, TO_CHAR(T.INS_MONEY) INS_MONEY, TO_CHAR(T.EQU_MONEY) EQU_MONEY, T.FIRST_PERSION FROM INSUR_REMIND T
T.PRO_CODE LIKE '%${PRO_CODE}%' AND T.SUPP_NAME LIKE '%${SUPP_NAME}%' AND T.EQU_NAME LIKE '%${EQU_NAME}%' AND T.CAR_SYMBOL LIKE '%${CAR_SYMBOL}%' AND T.FACTORY_NO LIKE '%${FACTORY_NO}%' AND (T.CUST_NAME LIKE '%${CUST_NAME}%' OR T.CUST_NAME_END LIKE '%${CUST_NAME}%') AND T.STATUS = 30 AND EST_DATE <= (SYSDATE-20)
ORDER BY EST_DATE ) T1 ) T2 WHERE T2.RN BETWEEN #{PAGE_BEGIN} AND #{PAGE_END}
